const minimumTime = 0;
const maximumTime = 50;

function fisherYatesNumber(min, max) {
  const numbers = [];
  for (let i = min; i <= max; i++) {
    numbers.push(i);
  }

  for (let i = numbers.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }

  return numbers[0];
}

const randomNum = fisherYatesNumber(minimumTime, maximumTime);

return [
  {
    json: {
      randomNum
    }
  }
];
